INSERT INTO `detalleactividades` VALUES ('13', 'DURANTE EL PERIODO QUE SE REPORTA SE INFORMA QUE A TRAVÉS DE LA DIRECCIÓN EJECUTIVA DE ASUNTOS JURÍDICOS Y EN REPRESENTACIÓN LEGAL DEL OPLE, SE REALIZARON LAS ACTIVIDADES SIGUIENTES:\r\nSE REALIZARON 2 CONVENIOS, 5 CONTRATOS SOLICITADOS POR DIVERSAS ÁREAS ADMINISTRATIVAS, SE COADYUVÓ EN LA ELABORACIÓN DE 3 ACUERDOS DEL CONSEJO GENERAL, SE COMPARECIÓ EN 3 AUDIENCIAS DE  DEMANDA Y EXCEPCIONES, OFRECIMIENTO Y ADMISIÓN DE PRUEBAS, SE PROMOVIÓ UN JUICIO DE AMPARO ADHESIVO, SE REALIZÓ LA SUSTANCIACIÓN DE 2 MEDIOS DE IMPUGNACIÓN, ASÍ COMO 4 PROCEDIMIENTOS ADMINISTRATIVOS SANCIONADORES.', 'INFORME DE ACTIVIDADES DE LA DIRECCIÓN EJECUTIVA DE ASUNTOS JURÍDICOS', 'SE SUPERVISA LA ADECUADA REPRESENTACIÓN LEGAL DEL OPLE EN VIRTUD DE LA FACULTAD OTORGADA  EN EL ARTÍCULO 115, FRACCIÓN I DEL CÓDIGO ELECTORAL LOCAL.', '2019', '1', '13', '2', '1', '1');
