INSERT INTO `detalleactividades` VALUES ('70', 'SE CONCLUYERON LOS TRABAJOS RELATIVOS AL CIERRE CONTABLE-PRESUPUESTAL DEL EJERCICIO FISCAL 2018, EMITIÉNDOSE, DE CONFORMIDAD CON LA LEY GENERAL DE CONTABILIDAD GUBERNAMENTAL Y DEMÁS DISPOSICIONES APLICABLES, LOS ESTADOS FINANCIEROS DEL OPLE AL 31 DE DICIEMBRE DE 2018. SE ELABORÓ EL INFORME DE LA GESTIÓN FINANCIERA DEL CUARTO TRIMESTRE DEL EJERCICIO FISCAL 2018, PRESENTADO POR LA SECRETARÍA EJECUTIVA Y APROBADO POR EL CONSEJO GENERAL, EN SESIÓN EXTRAORDINARIA DEL 22 DE ENERO DEL PRESENTE. SE INTEGRÓ EL INFORME DEL EJERCICIO DEL PRESUPUESTO DEL CUARTO TRIMESTRE 2018 Y EL CORRESPONDIENTE A LA LEY DE DISCIPLINA FINANCIERA DE LAS ENTIDADES FEDERATIVAS Y LOS MUNICIPIOS, PARA SU PRESENTACIÓN ANTE EL H. CONGRESO DEL ESTADO, EN CUMPLIMIENTO DE LA OBLIGACIÓN DE RENDICIÓN DE CUENTAS.', 'ESTADOS FINANCIEROS DICIEMBRE 2018\r\nINFORME DE GESTIÓN FINANCIERA DEL CUARTO TRIMESTRE DEL EJERCICIO 2018.\r\nINFORME DEL EJERCICIO DEL PRESUPUESTO CUARTO TRIMESTRE 2018.\r\nINFORME DE LEY DE DISCIPLINA FINANCIERA CUARTO TRIMESTRE 2018.', '', '2019', '1', '70', '6', '1', '1');
INSERT INTO `detalleactividades` VALUES ('71', 'DERIVADO DE LA ATENCIÓN DE 38 SOLICITUDES DE AUTORIZACIÓN DE RECURSOS POR EROGACIONES CORRESPONDIENTES A LA EJECUCIÓN DE ACTIVIDADES DEL PROGRAMA E101 DESARROLLO Y FORTALECIMIENTO INSTITUCIONAL DEL MES DE ENERO, SE REALIZÓ LA CLASIFICACIÓN, CODIFICACIÓN Y  REGISTRO DEL GASTO EN EL SISTEMA ÚNICO DE ADMINISTRACIÓN FINANCIERA DE ORGANISMOS PÚBLICOS DEL OPLE, EMITIÉNDOSE 171 ÓRDENES DE PAGO POR DIVERSOS CONCEPTOS, PARA REALIZAR EL PAGO OPORTUNO DE LAS MISMAS.', 'SOLICITUDES DE AUTORIZACIÓN DE RECURSOS DEL MES DE ENERO 2019, DEBIDAMENTE SOPORTADAS.\r\nÓRDENES PAGO \r\nCHEQUES Y TRANSFERENCIAS BANCARIAS.', '', '2019', '1', '71', '6', '1', '1');
