INSERT INTO `detalleactividades` VALUES ('167', 'SE AUXILIÓ EN LA ELABORACIÓN Y NOTIFICACIÓN DE LAS CONVOCATORIAS PARA LAS SESIONES DEL CONSEJO GENERAL DEL MES.', 'SESIÓN EXTRAORDINARIA DE FECHA 22/01/2019.\r\nDEL OPLEV/PCG/0005/2019 AL OPLEV/PCG/0018/2019;\r\nSESIÓN ORDINARIA DE FECHA 30/01/2019.\r\nDEL OPLEV/PCG/0028/2019 AL OPLEV/PCG/0041/2019;\r\nSESIÓN EXTRAORDINARIA DE FECHA 30/01/2019.\r\nDEL OPLEV/PCG/0043/2019 AL OPLEV/PCG/0056/2019.', 'SE SUPERO LO PROGRAMADO, EN RAZÓN DEL AUMENTO DEL NÚMERO DE SESIONES Y POR ENDE LAS NOTIFICACIONES A REALIZAR.', '2019', '1', '167', '14', '1', '1');
INSERT INTO `detalleactividades` VALUES ('168', 'SE AUXILIÓ EN LA ELABORACIÓN Y ENTREGA DE LOS GUIONES DE LAS SESIONES DEL CONSEJO GENERAL AL PRESIDENTE Y SECRETARIO DEL MISMO, PARA EL DESARROLLO DE LA SESIÓN.', 'GUIONES DE LAS SESIONES DE FECHA 22 Y 30/01/2019.', 'SE SUPERÓ LO PROGRAMADO, EN RAZÓN DEL AUMENTO DE SESIONES A LLEVARSE A CABO, POR VERLO NECESARIO LAS Y LOS INTEGRANTES DEL CONSEJO GENERAL. CABE MENCIONAR QUE ESTA UNIDAD CUENTA CON LOS GUIONES DE MANERA DIGITAL.', '2019', '1', '168', '14', '1', '2');
INSERT INTO `detalleactividades` VALUES ('169', 'SE COADYUVÓ CON LAS DIVERSAS ÁREAS EN LA ELABORACIÓN DE LOS PROYECTOS DE ACUERDOS PRESENTADOS EN LAS SESIONES DEL CONSEJO GENERAL.', 'ORDENES DEL DÍA DE LAS SESIONES DE FECHAS 22 Y 30/01/2019.', 'SE SUPERÓ LO PROGRAMADO, EN RAZÓN DE LO DETERMINADO POR LAS Y LOS INTEGRANTES DEL CONSEJO GENERAL, PARA ATENDER DIVERSOS TEMAS DE RELEVANCIA ELECTORAL.', '2019', '1', '169', '14', '1', '3');
INSERT INTO `detalleactividades` VALUES ('170', 'SE APOYÓ EN CIRCULAR EL MATERIAL DE CADA SESIÓN A LAS Y LOS INTEGRANTES DEL CONSEJO GENERAL, CON LA FINALIDAD DE QUE TUVIERAN CONOCIMIENTO DE LOS TEMAS A TRATAR CON ANTICIPACIÓN PARA SU DEBIDO ANÁLISIS Y DISCUSIÓN.', 'OFICIOS:\r\nDEL OPLEV/PCG/0005/2019 AL OPLEV/PCG/0018/2019;\r\nDEL OPLEV/PCG/0028/2019 AL OPLEV/PCG/0041/2019 Y,\r\nDEL OPLEV/PCG/0043/2019 AL OPLEV/PCG/0056/2019.', 'SE SUPERÓ LO PROGRAMADO, EN RAZÓN DEL AUMENTO DE LAS SESIONES DETERMINADAS POR LAS Y LOS INTEGRANTES DEL CONSEJO GENERAL PARA ATENDER DIVERSOS TEMAS DE RELEVANCIA ELECTORAL.', '2019', '1', '170', '14', '1', '4');
INSERT INTO `detalleactividades` VALUES ('171', 'SE ELABORARON Y NOTIFICARON LAS ACTAS DE LAS SESIONES DEL CONSEJO GENERAL, CON LA FINALIDAD DE SER ANALIZADAS Y, EN SU CASO, RECIBIR LAS OBSERVACIONES PERTINENTES.', 'OFICIOS.\r\nDEL OPLEV/UTS/091/2019 AL OPLEV/UTS/104/2019.', 'NO SE SUPERÓ LO PROGRAMADO, EN RAZÓN DE ATENDER DIVERSAS ACTIVIDADES POR ESTA UNIDAD, SIN EMBARGO, SE CUMPLIÓ CON EL PLAZO DETERMINADO EN EL ARTICULO 42 DEL REGLAMENTO DE SESIONES DEL CONSEJO GENERAL DE ESTE ÓRGANO ELECTORAL.', '2019', '1', '171', '14', '1', '5');
INSERT INTO `detalleactividades` VALUES ('172', 'SE AUXILIÓ EN LA NOTIFICACIÓN DE LAS SESIONES DE LAS COMISIONES DEL CONSEJO GENERAL, PARA SU DEBIDO DESARROLLO.', 'TARJETAS. COMISIONES DEL CONSEJO GENERAL.\r\n008/2019 - 031/2019 - 035/2019 - 007/2019 - 022/2019 - 021/2019 - 011/2019 - 023/2019 - 010/2019 - 019/2019 - 034/2019.', 'SE SUPERÓ LO PROGRAMADO, EN RAZÓN DE QUE LAS Y LOS INTEGRANTES DE LAS COMISIONES TUVIERON QUE ATENDER DIVERSOS TEMAS DE RELEVANCIA ELECTORAL. EN EL CASO DE LA JUNTA GENERAL EJECUTIVA, NO SE NOS FUE SOLICITADO EL NOTIFICAR SUS SESIONES.', '2019', '1', '172', '14', '1', '6');
