INSERT INTO `detalleactividades` VALUES ('269', 'DURANTE EL MES DE FEBRERO EL SECRETARIO EJECUTIVO DEL OPLE, SUPERVISÓ LA ELABORACIÓN DE 24 ACUERDOS, MISMOS QUE FUERON SOMETIDOS A CONSIDERACIÓN DE LOS INTEGRANTES DEL CONSEJO GENERAL EN SESIÓN ORDINARIA Y EXTRAORDINARIAS CORRESPONDIENTE AL MES QUE SE REPORTA.', 'ORDEN DEL DIA Y ACUERDOS DEL OPLEV/CG010/2019 AL OPLEV/CG033/2019, ASÍ COMO MINUTAS Y VERSIONES ESTENOGRÁFICAS EN RESGUARDO DE LAS UNIDADES TÉCNICAS DE SECRETARIADO Y DE COMUNICACIÓN SOCIAL.', '', '2019', '2', '11', '2', '1', '1');
INSERT INTO `detalleactividades` VALUES ('270', 'DURANTE EL MES DE ENERO EL SECRETARIO EJECUTIVO DEL OPLE, PRESENTÓ LOS INFORMES CORRESPONDIENTE, MISMOS QUE FUERON SOMETIDOS A CONSIDERACIÓN DE LOS INTEGRANTES DEL CONSEJO GENERAL EN SESIONES ORDINARIAS  CORRESPONDIENTE AL MES QUE SE REPORTA.', 'INFORMES, ORDEN DEL DÍA, ASÍ COMO MINUTAS Y VERSIONES ESTENOGRÁFICAS EN RESGUARDO DE LAS UNIDADES TÉCNICAS DE SECRETARIADO Y DE COMUNICACIÓN SOCIAL.', '', '2019', '2', '12', '2', '1', '2');
INSERT INTO `detalleactividades` VALUES ('271', 'DURANTE EL PERIODO QUE SE REPORTA SE INFORMA QUE A TRAVÉS DE LA DIRECCIÓN EJECUTIVA DE ASUNTOS JURÍDICOS Y EN REPRESENTACIÓN LEGAL DEL OPLE, SE REALIZARON LAS ACTIVIDADES SIGUIENTES:\r\nSE REALIZARON 7 COTEJOS PARA CERTIFICACIÓN DE LA SECRETARÍA EJECUTIVA, SE ELABORARON 2 CONTRATOS, SE ELABORARON 2 PROYECTOS DE ACUERDO DEL CONSEJO GENERAL, SE ATENDIERON 5 REQUERIMIENTOS REALIZADOS POR DIVERSAS AUTORIDADES JURISDICCIONALES, SE COMPARECIÓ A 2 AUDIENCIA DE DEMANDA Y EXCEPCIONES.', 'INFORME DE ACTIVIDADES DE LA DIRECCIÓN EJECUTIVA DE ASUNTOS JURÍDICOS.', 'SE SUPERVISA LA ADECUADA REPRESENTACIÓN LEGAL DEL OPLE EN VIRTUD DE LA FACULTAD OTORGADA  EN EL ARTÍCULO 115 FRACCIÓN I DEL CÓDIGO ELECTORAL LOCAL.', '2019', '2', '13', '2', '1', '1');
