INSERT INTO `detalleactividades` VALUES ('286', 'EL SECRETARIO EJECUTIVO EN EL PRESENTE MES, PARTICIPÓ COMO PRESIDENTE DEL COMITÉ Y SUBCOMITÉ DE ADQUISICIONES, ARRENDAMIENTOS, ADMINISTRACIÓN Y ENAJENACIÓN DE BIENES MUEBLES DEL OPLE, EN SESIONES ORDINARIAS CELEBRADAS EL 25 DE FEBRERO Y EXTRAORDINARIA DE FECHA 13 DE FEBRERO DEL AÑO EN CURSO.', 'ACTA DE LA SESIÓN ORDINARIA DEL COMITÉ Y SUBCOMITÉ, ASÍ COMO EXTRAORDINARIA DEL SUBCOMITÉ EN RESGUARDO DE LA DIRECCIÓN EJECUTIVA DE ADMINISTRACIÓN.', 'EN OBSERVANCIA AL ARTÍCULO 193 Y 195 DEL REGLAMENTO DE ADMINISTRACIÓN DEL OPLE SE CUMPLE CON EL BUEN DESEMPEÑO EN LAS ACTIVIDADES DEL COMITÉ Y SUBCOMITÉ AL REALIZAR LAS SESIONES ORDINARIAS Y EXTRAORDINARIA.', '2019', '2', '28', '2', '1', '3');
