INSERT INTO `detalleactividades` VALUES ('289', 'EN CUMPLIMIENTO A LOS REQUERIMIENTOS FORMULADOS A ESTA SECRETARÍA Y ANTE LA SOLICITUD DE DIVERSOS INTEGRANTES DEL CONSEJO GENERAL, ASÍ COMO A LOS  DE DIVERSOS ÓRGANOS JURISDICCIONALES SE COTEJÓ, RUBRICÓ Y SELLÓ LA CERTIFICACIÓN DE DOCUMENTOS.', 'SE ASENTÓ UN ACTA, EMITIÉNDOSE LA COPIA CERTIFICADA, Y SE REALIZÓ UNA CERTIFICACIÓN DE TERMINO.', 'LA UNIDAD TÉCNICA DE OFICIALÍA ELECTORAL ES LA  ENCARGADA DE COORDINAR DICHA FUNCIÓN Y COADYUVAR CON EL SECRETARIO EJECUTIVO PARA EL CUMPLIMIENTO DE SU OBJETIVO, EL CUAL CONSISTE EN GARANTIZAR A LOS PARTIDOS POLÍTICOS, CANDIDATOS INDEPENDIENTES Y ÓRGANOS EJECUTIVOS DEL OPLE VERACRUZ, EL ACCESO PRONTO Y EXPEDITO DE LA FE PÚBLICA ELECTORAL.', '2019', '2', '31', '2', '1', '2');
