INSERT INTO `detalleactividades` VALUES ('328', 'SE ELABORARON LOS ESTADOS FINANCIEROS DEL MES DE ENERO DEL EJERCICIO FISCAL 2019, PARA ELLO SE CONCILIÓ PREVIAMENTE, LA INFORMACIÓN CONTABLE CON LOS REGISTROS DEL MÓDULO DE CUENTAS POR PAGAR Y SE VALIDARON LOS SALDOS CON EL ESTADO DEL EJERCICIO DEL PRESUPUESTO DE EGRESOS AL 31 DE ENERO DE 2019. DEL MISMO MODO, SE REALIZÓ LA INTEGRACIÓN DE CUENTAS CONTABLES PARA SU REVISIÓN Y VALIDACIÓN, PROCEDIENDO FINALMENTE A LA DEPURACIÓN DE SALDOS DE LAS CUENTAS DEL EJERCICIO ACTUAL Y AÑOS ANTERIORES.', 'ESTADOS FINANCIEROS DEL MES DE ENERO DE 2018. \r\nARCHIVO ELECTRÓNICO DE INTEGRACIÓN DE CUENTAS CONTABLES.', '', '2019', '2', '70', '6', '1', '1');
INSERT INTO `detalleactividades` VALUES ('329', 'DERIVADO DE LA ATENCIÓN DE 61 SOLICITUDES DE AUTORIZACIÓN DE RECURSOS POR EROGACIONES CORRESPONDIENTES A LA EJECUCIÓN DE ACTIVIDADES DEL PROGRAMA E101 DESARROLLO Y FORTALECIMIENTO INSTITUCIONAL DEL MES DE FEBRERO, SE REALIZÓ LA CLASIFICACIÓN, CODIFICACIÓN Y  REGISTRO DEL GASTO EN EL SISTEMA ÚNICO DE ADMINISTRACIÓN FINANCIERA DE ORGANISMOS PÚBLICOS DEL OPLE, EMITIÉNDOSE 150 ÓRDENES DE PAGO POR DIVERSOS CONCEPTOS, PARA REALIZAR EL PAGO OPORTUNO DE LAS MISMAS.', 'SOLICITUDES DE AUTORIZACIÓN DE RECURSOS DEL MES DE FEBRERO DEBIDAMENTE SOPORTADAS, ÓRDENES PAGO Y TRANSFERENCIAS BANCARIAS.', '', '2019', '2', '71', '6', '1', '1');
