INSERT INTO `detalleactividades` VALUES ('331', 'SE EFECTUÓ LA SEGUNDA SESIÓN ORDINARIA DEL SUBCOMITÉ DE ADQUISICIONES, ARRENDAMIENTOS, ADMINISTRACIÓN Y ENAJENACIÓN DE BIENES MUEBLES DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ, EL DÍA 25 DE FEBRERO DE 2019.\r\nASIMISMO, EL 13 DE FEBRERO DEL PRESENTE AÑO, SE REALIZÓ LA TERCERA SESIÓN EXTRAORDINARIA DEL CITADO SUBCOMITÉ.', 'INVITACIÓN PARA LA SESIÓN ORDINARIA DEL SUBCOMITÉ DE ADQUISICIONES, ARRENDAMIENTOS, ADMINISTRACIÓN Y ENAJENACIÓN DE BIENES MUEBLES DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ.  \r\nORDEN DEL DÍA Y MATERIAL SOPORTE DE LA SESIÓN ORDINARIA DEL SUBCOMITÉ.\r\nACTA DE LA SESIÓN ORDINARIA. \r\nINVITACIÓN PARA LA SESIÓN EXTRAORDINARIA DEL SUBCOMITÉ DE ADQUISICIONES, ARRENDAMIENTOS, ADMINISTRACIÓN Y ENAJENACIÓN DE BIENES MUEBLES DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ.\r\nORDEN DEL DÍA Y MATERIAL SOPORTE DE LA TERCERA SESIÓN EXTRAORDINARIA DEL SUBCOMITÉ.\r\nACTA DE LA TERCERA SESIÓN EXTRAORDINARIA DEL SUBCOMITÉ.', '', '2019', '2', '73', '6', '1', '1');
INSERT INTO `detalleactividades` VALUES ('332', 'SE ATENDIERON 24 SOLICITUDES DE MATERIALES PRESENTADAS POR DIFERENTES ÁREAS DEL OPLE, 27 DE INSUMOS DE PAPELERÍA Y DIVERSOS, 29 DE SERVICIOS GENERALES Y/O MANTENIMIENTO, ASÍ COMO DE BIENES MUEBLES.', 'SOLICITUDES POR ESCRITO DE LAS ÁREAS DEL OPLE.\r\nVALES DE ENTREGA CORRESPONDIENTES A MATERIAL, INSUMOS, SERVICIOS GENERALES Y/O MANTENIMIENTO, DE CADA SOLICITUD DE LAS ÁREAS DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ.\r\nRESGUARDO SEMESTRAL Y/O TEMPORAL, ASÍ COMO VALES DE RESGUARDO PARA LA DOTACIÓN DE BIENES MUEBLES.', '', '2019', '2', '74', '6', '1', '2');
