INSERT INTO `detalleactividades` VALUES ('451', 'EN COORDINACIÓN CON LA COMISIÓN ESPECIAL DE IGUALDAD DE GÉNERO Y NO DISCRIMINACIÓN SE REALIZÓ UNA PLÁTICA SOBRE EL “USO DEL LENGUAJE INCLUYENTE Y NO SEXISTA”.  ASÍ COMO TAMBIÉN EL SEGUIMIENTO DEL CÓDIGO DE ÉTICA, IMPARTIDA POR LA INSTRUCTORA NAYELI VELASCO DE LA ROSA, CERTIFICADA POR EL CONSEJO NACIONAL DE NORMALIZACIÓN Y CERTIFICACIÓN DE COMPETENCIAS LABORALES. EN ESE SENTIDO Y PARTIENDO DE LAS ACCIONES QUE ES ATENDER LAS CORRECCIONES DE FUNDAMENTOS BÁSICOS QUE SUSTENTE EL USO NO SEXISTA Y NO DISCRIMINATORIO, SE PERMITE NOMBRAR A TRAVÉS DEL LENGUAJE INSTITUCIONAL Y ORGANIZACIONAL A MUJERES Y HOMBRES DE UNA MANERA NO ESTEREOTIPADA, ADEMÁS DE INCLUIR A TODAS AQUELLAS PERSONAS SUSCEPTIBLES DE SER DISCRIMINADAS POR RAZÓN DE GÉNERO.', 'OFICIO: OPLE/UTIGEI /0009/2019\r\n\r\nOPLEV/UTIGEI/027/2019\r\n\r\nPORTAFOLIO FOTOGRÁFICO.', '', '2019', '2', '193', '16', '1', '3');
