INSERT INTO `detalleactividades` VALUES ('460', 'SE REALIZÓ UN FORO EN EL MARCO DEL DÍA DE LA MUJER MEXICANA, EN COORDINACIÓN CON LA COMISIÓN ESPECIAL DE IGUALDAD DE GÉNERO Y NO DISCRIMINACIÓN DONDE SE DIO A CONOCER LAS EXPERIENCIAS LAS EXPERIENCIAS DE MUJERES QUE SE ENCUENTRAN EN UN CARGO POLÍTICO CON FUNDAMENTO EN EL RECONOCIMIENTO DE SUS DERECHOS POLÍTICOS ELECTORALES.\r\nASIMISMO, SE HIZO UN RECORDATORIO PARA CREAR CONCIENCIA EN LA POBLACIÓN SOBRE LA IMPORTANCIA DE LA IGUALDAD DE GÉNERO Y EL PAPEL FUNDAMENTAL DE LAS MUJERES TANTO EN LO PÚBLICO COMO EN LO PRIVADO.', 'OFICIOS: OPLEV/PCG/0094-0129/2019\r\n\r\nOPLEV/PCG/0133-0135/2019\r\n\r\nPORTAFOLIO FOTOGRÁFICO.', '', '2019', '2', '202', '16', '1', '1');
