INSERT INTO `detalleactividades` VALUES ('473', 'DE CONFORMIDAD CON LA LEY DE LA MATERIA, ASÍ COMO EL PROGRAMA OPERATIVO ANUAL, SE EFECTUARON LOS TRÁMITES INTERNOS NECESARIOS DIRIGIDOS A LA SECRETARÍA TÉCNICA DEL COMITÉ DE TRANSPARENCIA, CON LA FINALIDAD DE ATENDER LOS REQUERIMIENTOS DE LAS DIVERSAS ÁREAS, EN ATENCIÓN A SOLICITUDES DE INFORMACIÓN Y ACTUALIZACIONES DE PORTAL DE TRANSPARENCIA.', '•	MEMO NO. OPLEV/UTT/174/2019 DE FECHA 07 DE FEBRERO DE 2019.\r\n•	MEMO NO. OPLEV/UTT/231/2019 DE FECHA 12 DE FEBRERO DE 2019.', '', '2019', '2', '215', '17', '1', '5');
INSERT INTO `detalleactividades` VALUES ('474', 'EN EL MES DE FEBRERO DE DOS MIL DIECINUEVE, SE RECIBIERON 26 SOLICITUDES DE INFORMACIÓN A LAS CUALES SE LES DIO EL TRÁMITE CORRESPONDIENTE, SOLVENTÁNDOSE EN TIEMPO Y FORMA, DE CONFORMIDAD CON LO ESTABLECIDO EN LA LEY DE TRANSPARENCIA Y ACCESO A LA INFORMACIÓN PÚBLICA PARA EL ESTADO DE VERACRUZ DE IGNACIO DE LA LLAVE, VERSANDO SOBRE LOS TEMAS DE RESULTADOS ELECTORALES, CUESTIONES ADMINISTRATIVAS Y PARTIDOS POLÍTICOS.\r\nASIMISMO, SE RECIBIÓ UN TOTAL DE 2 LLAMADAS, QUE FUERON DEBIDAMENTE ATENDIDAS MEDIANTE EL SISTEMA FON-OPLEV, DENTRO DEL REPORTE DE LLAMADAS RECIBIDAS Y SOLVENTADAS, SE DETALLA EL NOMBRE DE LA PERSONA QUE LLAMA, SU PROCEDENCIA, TEMA QUE SE ATENDIÓ Y FECHA DE ATENCIÓN.', 'REPORTE DE SOLICITUDES DONDE SE DETALLA DESCRIPCIÓN DE LA SOLICITUD, RESULTADO, FORMA DE CONTESTACIÓN, COSTO Y TIEMPO DE RESPUESTA (DÍAS HÁBILES) EL CUAL PUEDE CORROBORARSE CON EL OFICIO NO. OPLEV/UTT/334/2019, REMITIDO A LA PRESIDENCIA DEL CONSEJO GENERAL, ASÍ COMO REPORTE DE FON-OPLEV DE MANERA IMPRESA.', '', '2019', '2', '216', '17', '1', '6');
