INSERT INTO `detalleactividades` VALUES ('487', 'PERSONAL DE LA SUBCONTRALORÍA DE AUDITORÍA Y CONTROL INTERNO DE ESTE ÓRGANO DE CONTROL ASISTIÓ A LAS SIGUIENTES CONFERENCIAS Y CURSOS: \r\n- CONFERENCIA “EVOLUCIÓN DEMOCRÁTICA EN LA CONSTITUCIÓN POLÍTICA DE LOS ESTADOS UNIDOS MEXICANOS EN SU 102 ANIVERSARIO”, EN EL AUDITORIO LEONARDO PASQUEL DE ESTE ORGANISMO ELECTORAL, EN FECHA 05 DE FEBRERO DEL PRESENTE AÑO.\r\n- CURSO “REFORMAS FISCALES 2019” EN LAS INSTALACIONES DEL IMAC, EN FECHA 13 DE FEBRERO DEL PRESENTE AÑO, ORGANIZADO POR LA SECRETARIA DE FINANZAS Y PLANEACIÓN. \r\n- CURSO “IMPORTANCIA DE LAS ACTITUDES PARA EL MEJOR DESEMPEÑO LABORAL”, EN EL AUDITORIO LEONARDO PASQUEL DE ESTE ORGANISMO ELECTORAL, EN FECHA 19 DE FEBRERO DEL PRESENTE AÑO.\r\n- CURSO “APLICACIÓN DE LA PROGRAMACIÓN NEUROLINGÜÍSTICA A LA COMUNICACIÓN Y EL APRENDIZAJE”, EN EL AUDITORIO LEONARDO PASQUEL DE ESTE ORGANISMO ELECTORAL, EN FECHAS 25 Y 26 DE FEBRERO DEL PRESENTE AÑO.\r\n- FORO “CAMINANDO JUNTAS HACIA EL PODER” EN EL AUDITORIO LEONARDO PASQUEL DE ESTE ORGANISMO ELECTORAL, EN FECHA 15 DE FEBRERO DEL PRESENTE AÑO.\r\n - TALLER “TRANSFERENCIA PRIMARIA”, EN LA SALA DE JUNTAS DEL EDIFICIO UBICADO EN JUÁREZ NO. 83 DE ESTE ORGANISMO ELECTORAL, DE FECHA 26 DE FEBRERO DEL PRESENTE AÑO.\r\n - CINE DEBATE SOBRE DOCUMENTAL “LA HISTORIA INVISIBLE” EN EL AUDITORIO LEONARDO PASQUEL DE ESTE ORGANISMO ELECTORAL, EN FECHA 22 DE FEBRERO DEL PRESENTE AÑO.\r\n- PLATICA INFORMATIVA “BRECHAS DE GENERO Y LAS ACCIONES AFIRMATIVAS ESENCIALES PARA SU ELIMINACIÓN, EN LA SALA DE JUNTAS DEL EDIFICIO UBICADO EN JUÁREZ NO. 83 DE ESTE ORGANISMO ELECTORAL, DE FECHA 28 DE FEBRERO DEL PRESENTE AÑO.\r\n- PLATICA “USO DEL LENGUAJE INCLUYENTE Y NO SEXISTA”, EN EL AUDITORIO LEONARDO PASQUEL DE ESTE ORGANISMO ELECTORAL, DE FECHA 27 DE FEBRERO DEL PRESENTE AÑO.', 'OF.  DECEYEC/038/2019.\r\nOF.  OPLEV/UTCFD/080/2019.\r\nOF.  OPLEV/UTCFD/080/2019.\r\nCIRCULAR 009 DE SE/2019.\r\nOF.  OPLEV/UTT/0259/2019.\r\nCIRCULAR 012 S.E.\r\nCIRCULAR 013 S.E.\r\nOF.  OPLEV/UTEGEI/027/2019.', 'EN ESTE MES PERSONAL DE LA SUBCONTRALORÍA DE AUDITORÍA Y CONTROL INTERNO DE ESTE ÓRGANO DE CONTROL ASISTIÓ A NUEVE CURSOS DE CAPACITACIÓN Y FORMACIÓN.', '2019', '2', '229', '18', '1', '90');
INSERT INTO `detalleactividades` VALUES ('488', '- SE PARTICIPÓ EN DIVERSAS SESIONES, CORRESPONDIENTES A LA INSTALACIÓN Y PRIMERA SESIÓN ORDINARIA DEL COMITÉ; ASÍ COMO EN LA TERCERA SESIÓN EXTRAORDINARIA Y EN LA SEGUNDA SESIÓN ORDINARIA DEL SUBCOMITÉ.', 'OF. OPLE/COMITÉ/0393/2019 DE FECHA 21/FEB/2019.\r\nACTA DEL COMITÉ DE ADQUISICIONES CORRESPONDIENTE A LA PRIMERA SESIÓN ORDINARIA.\r\n\r\nOF. OPLE/SUBCOMITÉ/0323/2019 DE FECHA 12/FEB/2019.\r\nACTA DEL SUBCOMITÉ DE ADQUISICIONES CORRESPONDIENTE A LA TERCERA SESIÓN ORDINARIA.\r\n\r\nOF. OPLE/SUBCOMITÉ/0405/2019 DE FECHA 21/FEB/2019.\r\nACTA DEL SUBCOMITÉ DE ADQUISICIONES CORRESPONDIENTE A LA SEGUNDA SESIÓN ORDINARIA.', 'EN ESTE MES SE REALIZARON TRES SESIONES, UNA SESIÓN ORDINARIA DEL COMITÉ Y DOS SESIONES EXTRAORDINARIAS DEL SUBCOMITÉ.', '2019', '2', '230', '18', '1', '1');
