INSERT INTO `detalleactividades` VALUES ('527', 'DURANTE EL MES DE MARZO EL SECRETARIO EJECUTIVO DEL OPLE, SUPERVISÓ LA ELABORACIÓN DE 4 ACUERDOS Y 5 ACTAS DE SESIONES DEL CONSEJO GENERAL, MISMOS QUE FUERON SOMETIDOS A CONSIDERACIÓN DE LOS INTEGRANTES DEL CONSEJO GENERAL EN SESIÓN ORDINARIA Y EXTRAORDINARIAS CORRESPONDIENTE AL MES QUE SE REPORTA.', 'ORDEN DEL DÍA Y ACUERDOS DEL OPLEV/CG034/2019 AL OPLEV/CG037/2019, ASÍ COMO MINUTAS Y VERSIONES ESTENOGRÁFICAS EN RESGUARDO DE LAS UNIDADES TÉCNICAS DE SECRETARIADO Y DE COMUNICACIÓN SOCIAL.', '', '2019', '3', '11', '2', '1', '1');
