INSERT INTO `detalleactividades` VALUES ('529', 'DURANTE EL PERIODO QUE SE REPORTA SE INFORMA QUE A TRAVÉS DE LA DIRECCIÓN EJECUTIVA DE ASUNTOS JURÍDICOS Y EN REPRESENTACIÓN LEGAL DEL OPLE, SE REALIZARON LAS ACTIVIDADES SIGUIENTES:\r\nSE REALIZARON 9 COTEJOS PARA CERTIFICACIÓN DE LA SECRETARÍA EJECUTIVA, SE COMPARECIÓ A 5 AUDIENCIAS ANTE EL TRIBUNAL ESTATAL DE CONCILIACIÓN Y ARBITRAJE DEL ESTADO DE VERACRUZ, SE CONTESTÓ LA DEMANDA CIVIL PROMOVIDA POR DULCE MARÍA LLANOS ROCHA, SE DIO CUMPLIMIENTO A UN REQUERIMIENTO DENTRO DEL EXPEDIENTE NÚMERO 771/2017/3°/II DEL JUICIO CONTENCIOSOS ADMINISTRATIVO.', 'INFORME DE ACTIVIDADES DE LA DIRECCIÓN EJECUTIVA DE ASUNTOS JURÍDICOS.', 'SE SUPERVISA LA ADECUADA REPRESENTACIÓN LEGAL DEL OPLE EN VIRTUD DE LA FACULTAD OTORGADA  EN EL ARTÍCULO 115 FRACCIÓN I DEL CÓDIGO ELECTORAL LOCAL.', '2019', '3', '13', '2', '1', '1');
