INSERT INTO `detalleactividades` VALUES ('544', 'EL SECRETARIO EJECUTIVO EN EL PRESENTE MES, PARTICIPÓ COMO PRESIDENTE DEL COMITÉ Y SUBCOMITÉ DE ADQUISICIONES, ARRENDAMIENTOS, ADMINISTRACIÓN Y ENAJENACIÓN DE BIENES MUEBLES DEL OPLE, EN SESIONES EXTRAORDINARIA DE FECHA 13 DE MARZO Y ORDINARIA CELEBRADA EL 25 DE MARZO DEL AÑO EN CURSO.', 'ACTA DE LA SESIÓN ORDINARIA Y EXTRAORDINARIA DEL COMITÉ Y SUBCOMITÉ EN RESGUARDO DE LA DIRECCIÓN EJECUTIVA DE ADMINISTRACIÓN.', 'EN OBSERVANCIA AL ARTÍCULO 193 Y 195 DEL REGLAMENTO DE ADMINISTRACIÓN DEL OPLE SE CUMPLE CON EL BUEN DESEMPEÑO EN LAS ACTIVIDADES DEL COMITÉ Y SUBCOMITÉ AL REALIZAR LAS SESIONES ORDINARIAS Y EXTRAORDINARIA.', '2019', '3', '28', '2', '1', '3');
