INSERT INTO `detalleactividades` VALUES ('546', 'SE EJERCIÓ LA FUNCIÓN DE OFICIALÍA ELECTORAL DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ A TRAVÉS DE LA UNIDAD TÉCNICA DE OFICIALÍA ELECTORAL, SE RECIBIERON UN TOTAL DE TRES PETICIONES, SE ASENTARON TRES ACTAS, SE REALIZARON NUEVE REGISTROS.', 'INFORME MENSUAL DE ACTIVIDADES DE LA UNIDAD TÉCNICA DE OFICIALÍA ELECTORAL DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ, SE ASENTÓ TRES ACTAS, EMITIÉNDOSE LAS COPIAS CERTIFICADAS.', 'LA UNIDAD TÉCNICA DE OFICIALÍA ELECTORAL ES LA  ENCARGADA DE COORDINAR DICHA FUNCIÓN Y COADYUVAR CON EL SECRETARIO EJECUTIVO PARA EL CUMPLIMIENTO DE SU OBJETIVO, EL CUAL CONSISTE EN GARANTIZAR A LOS PARTIDOS POLÍTICOS, CANDIDATOS INDEPENDIENTES Y ÓRGANOS EJECUTIVOS DEL OPLE VERACRUZ, EL ACCESO PRONTO Y EXPEDITO DE LA FE PÚBLICA ELECTORAL.', '2019', '3', '30', '2', '1', '1');
INSERT INTO `detalleactividades` VALUES ('547', 'EN CUMPLIMENTO A LOS REQUERIMIENTOS FORMULADOS A ESTÁ SECRETARÍA Y ANTE LA SOLICITUD DE DIVERSOS INTEGRANTES DEL CONSEJO GENERAL, ASÍ COMO A LOS DIVERSOS ÓRGANOS JURISDICCIONALES SE COTEJÓ, RUBRICÓ Y SELLO LA CERTIFICACIÓN DE DOCUMENTOS.', 'INFORME MENSUAL DE ACTIVIDADES DE LA UNIDAD TÉCNICA DE OFICIALÍA ELECTORAL DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ, ASÍ COMO EL REGISTRO DE COPIAS CERTIFICADAS LLEVADO EN LA SECRETARÍA EJECUTIVA DEL OPLE.', 'LA UNIDAD TÉCNICA DE OFICIALÍA ELECTORAL CONTRIBUYE CON LA SECRETARÍA EJECUTIVA PARA EL DEBIDO CUMPLIMIENTO DE LOS REQUERIMIENTOS SOLICITADOS POR LOS ÓRGANOS EJECUTIVOS DEL OPLE, PARTIDOS POLÍTICOS Y AUTORIDADES JURISDICCIONALES.', '2019', '3', '31', '2', '1', '2');
