INSERT INTO `detalleactividades` VALUES ('731', 'DE CONFORMIDAD CON LA LEY DE LA MATERIA, ASÍ COMO EL PROGRAMA OPERATIVO ANUAL, SE EFECTUARON LOS TRÁMITES INTERNOS NECESARIOS DIRIGIDOS A LA SECRETARÍA TÉCNICA DEL COMITÉ DE TRANSPARENCIA, CON LA FINALIDAD DE ATENDER LOS REQUERIMIENTOS DE LAS DIVERSAS ÁREAS, EN ATENCIÓN A SOLICITUDES DE INFORMACIÓN Y ACTUALIZACIONES DE PORTAL DE TRANSPARENCIA.', '•	MEMO NO. OPLEV/UTT/409/2019 DE FECHA 25 DE MARZO DE 2019.', '', '2019', '3', '215', '17', '1', '5');
INSERT INTO `detalleactividades` VALUES ('732', 'EN EL MES DE MARZO DE DOS MIL DIECINUEVE, SE RECIBIERON 19 SOLICITUDES DE INFORMACIÓN A LAS CUALES SE LES DIO EL TRÁMITE CORRESPONDIENTE, SOLVENTÁNDOSE EN TIEMPO Y FORMA, DE CONFORMIDAD CON LO ESTABLECIDO EN LA LEY DE TRANSPARENCIA Y ACCESO A LA INFORMACIÓN PÚBLICA PARA EL ESTADO DE VERACRUZ DE IGNACIO DE LA LLAVE, VERSANDO SOBRE LOS TEMAS DE RESULTADOS ELECTORALES, CUESTIONES ADMINISTRATIVAS Y PARTIDOS POLÍTICOS.\r\nASIMISMO, SE RECIBIÓ UN TOTAL DE 1 LLAMADA, QUE FUERON DEBIDAMENTE ATENDIDAS MEDIANTE EL SISTEMA FON-OPLEV, DENTRO DEL REPORTE DE LLAMADAS RECIBIDAS Y SOLVENTADAS, SE DETALLA EL NOMBRE DE LA PERSONA QUE LLAMA, SU PROCEDENCIA, TEMA QUE SE ATENDIÓ Y FECHA DE ATENCIÓN.', 'REPORTE DE SOLICITUDES DONDE SE DETALLA DESCRIPCIÓN DE LA SOLICITUD, RESULTADO, FORMA DE CONTESTACIÓN, COSTO Y TIEMPO DE RESPUESTA (DÍAS HÁBILES), EL CUAL SE DEMUESTRA CON EL MEMORÁNDUM NO. OPLEV/UTT/436/2019, ASÍ COMO REPORTE DE FON-OPLEV DE MANERA IMPRESA.', '', '2019', '3', '216', '17', '1', '6');
INSERT INTO `detalleactividades` VALUES ('733', 'CON LA FINALIDAD DE CUMPLIR CON EL PROGRAMA OPERATIVO ANUAL, SE REALIZÓ LA ELABORACIÓN DE UN CARTEL CON INFORMACIÓN GENERAL RESPECTO A LA IMPLEMENTACIÓN DEL ARCHIVO DEL ORGANISMO PÚBLICO LOCAL ELECTORAL DEL ESTADO DE VERACRUZ, EL CUAL FUE REMITIDO MEDIANTE MEMORANDUMS A LAS DIVERSAS ÁREAS QUE INTEGRAN ESTE ORGANISMO ELECTORAL.', 'MEMORANDUMS NO. OPLEV/UTT/0317/2019 AL NÚMERO OPLEV/UTT/333/2019 DE FECHA 07 DE MARZO DE 2019.', 'SE ELABORÓ UN OFICIO EXTRA, POR LO QUE SE CUENTA CON 17 DOCUMENTOS SOPORTE.', '2019', '3', '217', '17', '1', '7');
