INSERT INTO `detalleactividades` VALUES ('735', 'DURANTE LOS MESES DE ENERO, FEBRERO Y MARZO DEL PRESENTE AÑO, ESTA UNIDAD REALIZÓ LA TRAMITACIÓN EN TIEMPO Y FORMA ANTE LAS ÁREAS CORRESPONDIENTES DE LOS RECURSOS DE REVISIÓN INTERPUESTOS POR LOS CIUDADANOS, NOTIFICADOS A TRAVÉS DEL SISTEMA INFOMEX, ASÍ COMO AL SISTEMA DE NOTIFICACIONES DEL IVAI.\r\nASIMISMO, ATENDIENDO LAS RESOLUCIONES APROBADAS POR EL ÓRGANO GARANTE, SE DIO TRÁMITE Y CUMPLIMIENTO A LOS ORDENAMIENTOS RESPECTIVOS.', '•	ARCHIVOS ELECTRÓNICOS QUE CONTIENEN LA ELABORACIÓN DE LOS INFORMES DE RECURSO DE REVISIÓN.', '', '2019', '3', '219', '17', '1', '2');
