INSERT INTO `detalleactividades` VALUES ('738', 'PARA DAR CUMPLIMIENTO A LO ESTABLECIDO POR EL PROGRAMA OPERATIVO ANUAL, SE NOTIFICÓ A LAS DIVERSAS ÁREAS QUE INTEGRAN ESTE ORGANISMO ELECTORAL ACERCA DEL TALLER QUE OFRECE EL INSTITUTO VERACRUZANO DE ACCESO A LA INFORMACIÓN Y PROTECCIÓN DE DATOS PERSONALES, ACERCA DE LAS “OBLIGACIONES EN MATERIA DE DATOS PERSONALES”, DURANTE EL MES DE MARZO DEL PRESENTE AÑO, PARA QUE ACUDIERAN LOS ENLACES CORRESPONDIENTES.', 'MEMORANDUM NO. OPLEV/UTT/299/2019 AL OPLEV/UTT/304; DEL NÚMERO OPLEV/UTT/306/2019 AL OPLEV/UTT/309/2019, Y DEL OPLEV/UTT/404 AL OPLEV/UTT/408/2019 DE FECHA 11 Y 25 DE MARZO DE 2019.', '', '2019', '3', '222', '17', '1', '3');
