INSERT INTO `detalleactividades` VALUES ('746', '- SE PARTICIPÓ EN DIVERSAS SESIONES, CORRESPONDIENTES A LA PRIMERA SESIÓN EXTRAORDINARIA DEL COMITÉ; ASÍ COMO EN LA TERCERA SESIÓN ORDINARIA DEL SUBCOMITÉ.', 'OF. OPLE/COMITÉ/451/2019 DE FECHA 11/MAR/2019.\r\nACTA DEL COMITÉ DE ADQUISICIONES CORRESPONDIENTE A LA PRIMERA SESIÓN EXTRAORDINARIA.\r\n\r\nOF. OPLE/SUBCOMITÉ/477/2019 DE FECHA 20/MAR/2019.\r\nACTA DEL SUBCOMITÉ DE ADQUISICIONES CORRESPONDIENTE A LA TERCERA SESIÓN ORDINARIA.', 'EN ESTE MES SE PRESENTA UN INCREMENTO EN LA META, TODA VEZ QUE SE REALIZARON DOS SESIONES, UNA ORDINARIA Y UNA EXTRAORDINARIAS.', '2019', '3', '230', '18', '1', '1');
INSERT INTO `detalleactividades` VALUES ('747', '- SE EMITIÓ UNA PROPUESTA DE ACTUALIZACIÓN DEL PRESUPUESTO 2019 CON LA FINALIDAD DE QUE, EN EL EJERCICIO DE LA FUNCIÓN PÚBLICA, EL PERSONAL SE CONDUZCA CONFORME A LOS PRINCIPIOS DE DISCIPLINA Y AUSTERIDAD.\r\n\r\n- SE SOLICITÓ A LA DIRECCIÓN EJECUTIVA DE ADMINISTRACIÓN, COPIA DE LOS ESTADOS FINANCIEROS, COPIA DE ESTADO BANCARIO DE DIVERSAS CUANTAS, REPORTE A DETALLE OBTENIDO DEL MÓDULO DE CAJA DEL SUAFOP, COPIAS DE LAS CONCILIACIONES BANCARIAS, ASÍ COMO, DE LAS TRANSFERENCIAS ELECTRÓNICAS DE PAGO CON SU CORRESPONDIENTE SOPORTE DOCUMENTAL, DE LAS SIGUIENTES CONTRIBUCIONES: ISR RETENIDO DE SUELDOS Y SALARIOS, ISR RETENIDO DE HONORARIOS ASIMILADOS A SALARIOS, ISR RETENIDO DE ARRENDAMIENTO, ISR RETENIDO DE HONORARIOS PROFESIONALES IMPUESTO SOBRE EROGACIONES POR REMUNERACIONES AL TRABAJO; ARCHIVO EN DIGITAL DE LA PLANTILLA DEL PERSONAL DE ESTRUCTURA DEL ÓRGANO CENTRAL, DEL MISMO MODO, SE REQUIRIÓ PONER A DISPOSICIÓN PARA SU INSPECCIÓN FÍSICA EN LA DIRECCIÓN EJECUTIVA DE ADMINISTRACIÓN EL EXPEDIENTE DE LA NÓMINA DEL PERSONAL DE OFICINAS CENTRALES Y EL EXPEDIENTE DE LOS PAGOS DE IMSS, SAR E IPE Y LA CONCILIACIÓN (ORIGINAL) DE EXISTENCIAS DE ALMACÉN CORRESPONDIENTE AL MES DE ENERO DE 2019, PARA SU REVISIÓN Y ANÁLISIS.\r\n\r\n- SE REQUIRIÓ A LA UNIDAD TÉCNICA DE PLANEACIÓN LAS CÉDULAS DE INDICADORES APLICADOS POR LAS DISTINTAS ÁREAS DEL ORGANISMO, CORRESPONDIENTES A LOS MESES DE NOVIEMBRE Y DICIEMBRE DE 2018, PARA SU REVISIÓN.\r\n\r\n- SE PRACTICÓ EL ARQUEO AL FONDO FIJO EN LAS DIFERENTES DE ESTE ORGANISMO ELECTORAL.\r\n\r\n- SE REVISÓ LAS BITÁCORAS DE COMBUSTIBLE DEL PARQUE VEHICULAR.', 'OF. OPLE/OIC/375/2019.\r\n\r\nOF. OPLE/OIC/SAC/376/2019 DE FECHA 13/MAR/2019.\r\n\r\nOF. OPLE/OIC/SAC/381/2019 DE FECHA 15/MAR/2019.\r\n\r\nOF. OPLE/OIC/SAC/397/2019 DE FECHA 26/MAR/2019.\r\nOF. OPLE/OIC/SAC/398/2019 DE FECHA 26/MAR/2019.\r\nOF. OPLE/OIC/SAC/399/2019 DE FECHA 26/MAR/2019.\r\nOF. OPLE/OIC/SAC/400/2019 DE FECHA 26/MAR/2019.', 'EN ESTE MES SE PRESENTA UN INCREMENTO EN LA META, TODA VEZ QUE SE REALIZARON MAS PRUEBAS DE AUDITARÍA DE LAS PROGRAMADAS.', '2019', '3', '231', '18', '1', '2');
INSERT INTO `detalleactividades` VALUES ('748', '- SE DIO SEGUIMIENTO AL INFORME CORRESPONDIENTE AL MES DE MARZO, SOBRE LA FISCALIZACIÓN A LA CUENTA PÚBLICA 2017.\r\n\r\n- SE ASISTIÓ A LA “REUNIÓN PLENARIA DEL SISTEMA DE EVALUACIÓN Y FISCALIZACIÓN DE VERACRUZ”, DE ACUERDO A LOS MECANISMOS INSTITUCIONALES DE COORDINACIÓN QUE EL ORFIS HA ESTABLECIDO PARA EL FORTALECIMIENTO DE LAS ACCIONES DE CONTROL INTERNO, PREVENCIÓN Y CORRECCIÓN, CON LOS ÓRGANOS INTERNOS DE CONTROL DE LOS ENTES FISCALIZABLES.', 'OFS/AG_DGAJ/4051/03/2019 DE FECHA 01/MAR/19.\r\n\r\nINVITACIÓN POR MEDIO ELECTRÓNICO DE FECHA 15/MAR/19.', '', '2019', '3', '232', '18', '1', '3');
INSERT INTO `detalleactividades` VALUES ('749', '- SE REMITIERON LAS CÉDULAS DE PUESTOS CON LAS ADECUACIONES CORRESPONDIENTES, EN ATENCIÓN AL OFICIO NÚMERO OPLEV/UTP/DP/002/2019 DE LA UNIDAD TÉCNICA DE PLANEACIÓN, A TRAVÉS DEL CUAL SOLICITA A ESTE ÓRGANO INTERNO DE CONTROL LAS CÉDULAS DESCRIPTIVAS DE PUESTOS CON LAS MODIFICACIONES ACORDE A LA ESTRUCTURA Y/O NECESIDADES DE ESTA ÁREA.', 'OF. OPLE/UTP/DP/002/2019 DE FECHA 14/FEB/2019.', '', '2019', '3', '233', '18', '1', '4');
