INSERT INTO `detalleactividades` VALUES ('3166', 'SE PARTICIPÓ COMO SECRETARIA TÉCNICA DE LA COMISIÓN ESPECIAL DE INNOVACIÓN Y EVALUACIÓN, EN LA PRIMERA SESIÓN ORDINARIA Y SEGUNDA SESIÓN EXTRAORDINARIA, CELEBRADAS EN FECHA 29 DE MARZO DE 2019. EN LA PRIMERA SESIÓN ORDINARIA SE APROBÓ EL ACTA DE LA PRIMERA SESIÓN EXTRAORDINARIA; Y EN LA SEGUNDA SESIÓN EXTRAORDINARIA SE PRESENTÓ UN INFORME DE LOS AVANCES DE LOS TRABAJOS DE LA COMISIÓN.', 'PRIMERA SESIÓN ORDINARIA OFICIO NO. OPLEV/CEIYE-017/2019 AL OPLEV/CIYE-032/2019, ORDEN DEL DÍA Y ACTA DE LA SESIÓN EXTRAORDINARIA DE 22 DE ENERO DE 2019.\r\nSEGUNDA SESIÓN EXTRAORDINARIA OFICIO NO. OPLEV/CEIYE-033/2019 AL OPLEV/CIYE-048/2019, ORDEN DEL DÍA E INFORME DE LA COMISIÓN ESPECIAL DE INNOVACIÓN Y EVALUACIÓN.', 'LA PRESENTE ACTIVIDAD NO SE ENCONTRABA PROGRAMADA PARA ESTE MES; SIN EMBARGO, A SOLICITUD DE LA PRESIDENCIA DE LA COMISIÓN, SE CONVOCÓ A SESIÓN PARA ATENDER CUESTIONES PUNTUALES PARA EL DESARROLLO DE LAS ACTIVIDADES DE LA COMISIÓN.', '2019', '3', '270', '12', '1', '3');
