INSERT INTO `infocedulas` VALUES ('0', '1', '1', 'Consejo General', 'CG-E101-01P\r\n', 'Eficacia en la celebración de las sesiones del Consejo General.', 'Realizar con oportunidad las sesiones programadas por el seno del Consejo General, en donde se exponen temas de trabajo.', '100', 'Enero-Diciembre 2019', 'ENE-DIC', '', '1', '', '', '', '', 'SR= Sesiones realizadas          ', '', '', 'SP= Sesiones programadas ', '', '', '1', '', '', '', 'LIC. MITZI TORRES JUÁREZ                                              ', 'TITULAR DE LA UNIDAD TÉCNICA DEL SECRETARIADO');
INSERT INTO `infocedulas` VALUES ('0', '2', '1', 'Consejo General', 'CG-E101-02P', 'Eficacia en analizar y aprobar los proyectos de Acuerdos y/o Resoluciones sometidos ante el Consejo General.', 'Llevar a cabo un análisis y, en su caso, aprobar los Proyectos de Acuerdos y/o Resoluciones, que son presentados a las y los integrantes del Consejo General, mismos que serán posteriormente subidos al portal del OPLE.', '100', 'Enero-Diciembre 2019', 'ENE-DIC', '', '1', '', '', '', '', 'AA= Acuerdos y/o Resoluciones aprobados', '', '', 'AP= Acuerdos y/o Resoluciones presentados', '', '', '1', '', '', '', 'LIC. MITZI TORRES JUÁREZ                                              ', 'TITULAR DE LA UNIDAD TÉCNICA DEL SECRETARIADO');
INSERT INTO `infocedulas` VALUES ('0', '3', '1', 'Consejo General', 'CG-E101-03P', 'Eficacia en la aprobación de los proyectos de actas de sesiones del Consejo General.', 'Realizar con oportunidad y detenimiento un análisis sobre los Proyectos de Actas de Sesiones sometidas en sesiones para su posible aprobación por las y los integrantes del Consejo General, y subir dicho documento al portal de la página del OPLE.', '100', 'Enero-Diciembre 2019', 'ENE-DIC', '', '1', '', '', '', '', 'AA= Actas aprobadas', '', '', 'AP= Actas presentadas', '', '', '1', '', '', '', 'LIC. MITZI TORRES JUÁREZ                                              ', 'TITULAR DE LA UNIDAD TÉCNICA DEL SECRETARIADO');
INSERT INTO `infocedulas` VALUES ('0', '4', '1', 'Consejo General', 'CG-E101-04P', 'Eficacia en el análisis de los informes sometidos al Consejo General.', 'Realizar un análisis y posteriormente someter dichos informes a consideración de las y los integrantes del Consejo General para su posible modificación y/o aprobación por los mismos.', '100', 'Enero-Diciembre 2019', 'ENE-DIC', '', '1', '', '', '', '', 'IA= Informes presentados', '', '', 'IP= Informes programados', '', '', '1', '', '', '', 'LIC. MITZI TORRES JUÁREZ                                              ', 'TITULAR DE LA UNIDAD TÉCNICA DEL SECRETARIADO');
INSERT INTO `infocedulas` VALUES ('0', '5', '2', 'Secretaría Ejecutiva', 'SE-E102-01P', 'Eficiencia programática', 'Incrementar la calidad en el cumplimiento de las actividades del POA y la eficiencia en el uso de los recursos públicos. ', '100', 'Enero-Octubre 2019', 'ENE-OCT', '', '1', '', '', '', '', 'EPRG=Eficacia programática ', '', '', 'EPRP= Eficacia presupuestal', '', '', '2', '', '', '', 'Mtro. Hugo Enrique Castro Bernabe', 'Secretario Ejecutivo');
INSERT INTO `infocedulas` VALUES ('0', '6', '2', 'Secretaría Ejecutiva', 'SE-E102-02P', 'Eficacia en la elaboración de propuestas para la realización del POA', 'Identificar posibles variaciones entre el número de actividades propuestas por las áreas ejecutivas en relación al número de actividades que finalmente son aprobadas en el POA, para determinar necesidades de capacitación o mejoría en los instructivos y/o manuales que se emiten para su construcción. ', '100', 'Agosto 2019', 'AGO', '', '1', '', '', '', '', 'PA= Propuestas aprobadas.', '', '', 'PP= Propuestas programadas', '', '', '5', '', '', '', 'Mtro. Hugo Enrique Castro Bernabe', 'Secretario Ejecutivo');
INSERT INTO `infocedulas` VALUES ('0', '7', '2', 'Secretaría Ejecutiva', 'SE-E102-03P', 'Eficacia en la entrega del POA y Presupuesto', 'Presentar en tiempo y forma el Anteproyecto de presupuesto e integrar el Programa Operativo Anual del OPLE 2020. ', '100', 'Septiembre 2019', 'SEP', '', '1', '', '', '', '', 'PDR= Presentacion de Documentos realizado', '', '', 'PDP= Presentacion de Documentos programado', '', '', '5', '', '', '', 'Mtro. Hugo Enrique Castro Bernabe', 'Secretario Ejecutivo');
INSERT INTO `infocedulas` VALUES ('0', '8', '2', 'Secretaría Ejecutiva', 'SE-E102-04P', 'Supervisión de un estudio comparado de la reglamentación que rige la materia electoral en el OPLE. ', 'Establecer disposiciones aplicables en materia político electoral que permitan un ejercicio público sin contradicciones y vacios legales. ', '100', 'Diciembre 2019', 'DIC', '', '1', '', '', '', '', 'ER=Estudio Realizado', '', '', 'EP= Estudio Programado ', '', '', '5', '', '', '', 'Mtro. Hugo Enrique Castro Bernabe', 'Secretario Ejecutivo');
INSERT INTO `infocedulas` VALUES ('0', '9', '2', 'Secretaría Ejecutiva', 'SE-E102-05P', 'Documentación Electoral ', 'Coordinar la supervisión a través de la Dirección Ejecutiva de Organización Electoral respecto de la clasificación y separación del material y documentación electoral que se encuentra almacenado, así como determinar aquellos que serán destruidos y los que por encontrarse en buenas condiciones o resultar útiles para fines de investigación, deberán ser resguardados en las bodegas de este organismo.', '100', 'Diciembre 2019', 'DIC', '', '1', '', '', '', '', 'DR= Documento realizado ', '', '', 'DP= Documento Programado ', '', '', '5', '', '', '', 'Mtro. Hugo Enrique Castro Bernabe', 'Secretario Ejecutivo');
INSERT INTO `infocedulas` VALUES ('0', '10', '2', 'Secretaría Ejecutiva', 'SE-E102-06P', 'Supervisar la formulación de una propuesta de iniciativa de reforma a la Constitución Local y al Código Electoral. ', 'Coordinar a través de la Dirección Ejecutiva de Asuntos Jurídicos las propuestas de reforma a la Constitución Local, así como al Código Electoral que consideren las Direcciones Ejecutivas y Unidades Técnicas del OPLE, que permitan adecuar la legislación electoral local a las necesidades. ', '100', 'Diciembre 2019', 'DIC', '', '1', '', '', '', '', 'PA=Propuesta Aprobada', '', '', 'PPRE=Propuesta Presentada', '', '', '5', '', '', '', 'Mtro. Hugo Enrique Castro Bernabe', 'Secretario Ejecutivo');
