INSERT INTO `programasesp` VALUES ('1', '1', '1', 'E1 0101', 'Celebración de las Sesiones del Consejo General y sus Comisiones', 'Realizar las sesiones del Consejo General conforme a los principios de certeza, imparcialidad, independencia, legalidad, máxima publicidad y objetividad, así como la información y documentación sea entregada a las y los miembros del Consejo General de manera eficaz.', '1');
INSERT INTO `programasesp` VALUES ('2', '1', '2', 'E1 0102', 'Actividades Institucionales', 'Planear, diseñar e implementar los mecanismos operativos, administrativos y normativos que delimiten las atribuciones y deberes de las áreas ejecutivas del Organismo Público Local Electoral del Estado de Veracruz contribuyendo a su desarrollo y consolidación.', '2');
INSERT INTO `programasesp` VALUES ('3', '1', '3', 'E1 0103', 'Participar en la operación del Consejo General ', 'Garantizar la actuación del Secretario del Consejo General en términos de la normatividad aplicable.', '2');
INSERT INTO `programasesp` VALUES ('4', '1', '4', 'E1 0104', 'Representación Legal del Organismo Público Local Electoral del Estado de Veracruz', 'Implementar las acciones pertinentes para defender los intereses del Organismo Público Local Electoral del Estado de Veracruz.', '2');
INSERT INTO `programasesp` VALUES ('5', '1', '5', 'E1 0105', 'Participar en las actividades de la Junta General Ejecutiva', 'Garantizar la operatividad y adecuado desarrollo de las reuniones de la Junta General Ejecutiva.', '2');
INSERT INTO `programasesp` VALUES ('6', '1', '6', 'E1 0106', 'Coordinar Direcciones Ejecutivas y Unidades Técnicas', 'Apoyar en la implementación de mecanismos operativos para el adecuado ejercicio de las actividades de Direcciones Ejecutivas y Unidades Técnicas.', '2');
INSERT INTO `programasesp` VALUES ('7', '1', '7', 'E1 0107', 'Eficientar el ejercicio y transparentar el control presupuestal', 'Implementar los mecanismos administrativos que garanticen el correcto ejercicio presupuestal del Organismo.', '2');
INSERT INTO `programasesp` VALUES ('8', '1', '8', 'E1 0108', 'Ejercicio de la Fe Pública Electoral', 'Certificar actos, hechos o documentos relacionados con las atribuciones del Organismo Público Local Electoral del Estado de Veracruz.', '2');
INSERT INTO `programasesp` VALUES ('9', '1', '9', 'E1 0109', 'Cumplimiento de sanciones impuestas a los partidos políticos', 'Llevar a cabo las actividades encaminadas a dar cumplimiento a las obligaciones derivadas de los dictámenes de las autoridades electorales federales y  locales en cuanto al seguimiento y aplicación de sanciones y devolución de remanentes.', '3');
INSERT INTO `programasesp` VALUES ('10', '1', '10', 'E1 0110', 'Organizaciones Políticas', 'Llevar a cabo el procedimiento de registro de Comités Directivos, Representantes ante el Órgano Central, así como del otorgamiento o pérdidas del registro o acreditación de los Partidos Políticos, y Asociaciones Políticas estatales.', '3');
INSERT INTO `programasesp` VALUES ('11', '1', '11', 'E1 0111', 'Prerrogativas a Partidos Políticos y Asociaciones Políticas Estatales', 'Realizar los estudios inherentes al ejercicio de las prerrogativas a que tienen derecho los Partidos Políticos y Asociaciones Políticas.', '3');
INSERT INTO `programasesp` VALUES ('12', '1', '12', 'E1 0112', 'Archivo', 'Ordenar y sistematizar la información social y estadísticamente útil en materia electoral respecto a los procesos electorales 2016-2017, 2017-2018 y extraordinario 2018.', '4');
INSERT INTO `programasesp` VALUES ('13', '1', '13', 'E1 0113', 'Organización Electoral', 'Eficientar los recursos humanos y materiales en materia de organización electoral con miras al Proceso Electoral Local 2020-2021.', '4');
INSERT INTO `programasesp` VALUES ('14', '1', '14', 'E1 0114', 'Construcción de Ciudadanía', 'Fortalecer la cultura democrática en el Estado, a través del impulso de la participación ciudadana.', '5');
INSERT INTO `programasesp` VALUES ('15', '1', '15', 'E1 0115', 'Implementar Acciones para fortalecer la Estrategia Nacional de Educación Cívica 2017-2023', 'Desarrollar mecanismos operativos que permitan la realización de actividades de Educación Cívica.', '5');
INSERT INTO `programasesp` VALUES ('16', '1', '16', 'E1 0116', 'Actividades Institucionales ', 'Planear, diseñar e implementar los mecanismos operativos, administrativos y normativos que controlen y cumplan con las normas jurídicas en materia Contable, Presupuestal y Programática del Organismo Público Local Electoral del Estado de Veracruz.', '6');
INSERT INTO `programasesp` VALUES ('17', '1', '17', 'E1 0117', 'Administración y Gestión de Recursos Financieros', 'Fijar las directrices que permitan realizar las gestiones administrativas y financieras del Organismo Público Local Electoral del Estado de Veracruz, de manera interna y externa, para el  cumplimiento de las funciones y atribuciones que le son inherentes.', '6');
INSERT INTO `programasesp` VALUES ('18', '1', '18', 'E1 0118', 'Administración y Gestión de Recursos Materiales, Servicios Generales, Inventarios y Adquisiciones.', 'Realizar los procesos de adquisiciones y contratación de arrendamientos y servicios generales requeridos por las áreas del Organismo Público Local Electoral del Estado de Veracruz, para el cumplimiento de las funciones y atribuciones que le son inherentes, realizando los procesos de contratación en estricto apego a las disposiciones legales y administrativas aplicables.', '6');
INSERT INTO `programasesp` VALUES ('19', '1', '19', 'E1 0119', 'Administración y Gestión de Recursos Humanos ', 'Fijar las directrices que permitan realizar las gestiones administrativas y financieras del Organismo Público Local Electoral del Estado de Veracruz, de manera interna y externa, para el  cumplimiento de las funciones y atribuciones que le son inherentes.', '6');
INSERT INTO `programasesp` VALUES ('20', '1', '20', 'E1 0120', 'Consultas y Fortalecimiento al interior del Organismo ', 'Atender las consultas y fortalecer los procedimientos  normativos que delimiten las atribuciones y deberes de las áreas ejecutivas del Organismo Público Local Electoral del Estado de Veracruz.', '7');
INSERT INTO `programasesp` VALUES ('21', '1', '21', 'E1 0121', 'Contencioso no Electoral', 'Realizar de manera eficiente el trámite de los asuntos que se presenten en el Organismo Público Local Electoral del Estado de Veracruz.', '7');
INSERT INTO `programasesp` VALUES ('22', '1', '22', 'E1 0122', 'Medios de Impugnación y Procedimientos Administrativos Sancionadores', 'Dar trámite a los medios de impugnación y substanciar los procedimientos administrativos sancionadores que se presenten ante el Organismo Público Local Electoral del Estado de Veracruz.', '7');
INSERT INTO `programasesp` VALUES ('23', '1', '23', 'E1 0123', 'Actividades Institucionales', 'Participar y dar seguimiento a los Comités y Subcomités cuya atribución corresponde a la Unidad de Fiscalización del Organismo Público Local Electoral del Estado de Veracruz contribuyendo a su desarrollo y consolidación.', '8');
INSERT INTO `programasesp` VALUES ('24', '1', '24', 'E1 0124', 'Profesionalización de las Asociaciones Políticas', 'Brindar a las Asociaciones Políticas Estatales con registro vigente ante el Organismo Público Local Electoral del Estado de Veracruz la realización con éxito de los cambios en su contabilidad de acuerdo a la reglamentación.', '8');
INSERT INTO `programasesp` VALUES ('25', '1', '25', 'E1 0125', 'Registro contable y documentación comprobatoria', 'Brindar certeza sobre el origen uso y destino del financiamiento público que reciben las Asociaciones Políticas Estatales con registro vigente ante este Organismo Público Local Electoral.', '8');
INSERT INTO `programasesp` VALUES ('26', '1', '26', 'E1 0126', 'Actividades Institucionales', 'Difundir las acciones que realizan la Direcciones Ejecutivas y Unidades Técnicas del Organismo Público Local Electoral del Estado de Veracruz.', '9');
INSERT INTO `programasesp` VALUES ('27', '1', '27', 'E1 0127', 'Difusión de los programas institucionales', 'Difundir los programas institucionales del Organismo Público Local Electoral del Estado de Veracruz a través de diferentes estrategias de medios para lograr un mayor impacto.', '9');
INSERT INTO `programasesp` VALUES ('28', '1', '28', 'E1 0128', 'Eficientar el reporte de monitoreo a los medios de comunicación impresos y electrónicos ', 'Realizar el seguimiento de la información que se genera diariamente en los diferentes medios de comunicación para generar líneas de acción que contribuyan al posicionamiento del Organismo en la opinión pública.', '9');
INSERT INTO `programasesp` VALUES ('29', '1', '29', 'E1 0129', 'Actividades Institucionales', 'Diseñar y elaborar material de promoción y difusión del Organismo Público Local Electoral del Estado de Veracruz.', '10');
INSERT INTO `programasesp` VALUES ('30', '1', '30', 'E1 0130', 'Seguimiento al Servicio Profesional Electoral Nacional', 'Apoyar en el cumplimiento de los fines del Instituto Nacional Electoral y del Organismo Público Local Electoral del Estado de Veracruz, así como en el ejercicio de sus atribuciones, conforme a los principios rectores de la función electoral.', '10');
INSERT INTO `programasesp` VALUES ('31', '1', '31', 'E1 0131', 'Capacitación permanente a las áreas del Organismo Público Local Electoral del Estado de Veracruz', 'Diseñar y adaptar la impartición de cursos de Capacitación Formativa y actualización del personal del Organismo, para desarrollar un piso mínimo de competencias laborales, en las modalidades presenciales y en línea.', '10');
INSERT INTO `programasesp` VALUES ('32', '1', '32', 'E1 0132', 'Actividades Institucionales', 'Apoyo en el desarrollo de las actividades institucionales del Organismo Público Local Electoral del Estado de Veracruz.', '11');
INSERT INTO `programasesp` VALUES ('33', '1', '33', 'E1 0133', 'Asesoría y soporte técnico a las áreas del Organismo', 'Proporcionar asesoría y apoyo en materia de informática al Organismo Público Local Electoral del Estado de Veracruz en cuanto a operación de equipo de cómputo (paquetería, scanner, redes, etc.), así como el mantenimiento preventivo y correctivo de la infraestructura tecnológica, para optimizar el desempeño de sus  labores.', '11');
INSERT INTO `programasesp` VALUES ('34', '1', '34', 'E1 0134', 'Innovación, desarrollo, implementación y soporte de sistemas informáticos ', 'Innovar, Desarrollar y poner en marcha los sistemas informáticos en apoyo a las actividades electorales y administrativas de las diferentes áreas del Organismo Público Local Electoral del Estado de Veracruz.', '11');
