INSERT INTO `programasesp` VALUES ('40', '1', '40', 'E1 0140', 'Actividades Institucionales', 'Coordinar las acciones necesarias a fin de que las sesiones del Consejo General, de sus Comisiones, así como, de la Junta Ejecutiva se lleven a cabo  con los elementos materiales requeridos por sus integrantes respectivos.', '14');
