INSERT INTO `programasesp` VALUES ('55', '3', '1', 'E3 0301', 'Programa de Actualización en temas de Prerrogativas y Partidos Políticos', 'Realizar estudios sobre temas relevantes en materia de candidaturas independientes, acciones afirmativas y financiamiento público, que permita a la ciudadanía conocer la evolución de estas  instituciones en el estado de Veracruz y su impacto en la participación ciudadana.', '3');
INSERT INTO `programasesp` VALUES ('56', '3', '2', 'E3 0302', 'Participación Ciudadana y Educación Cívica', 'Implementar actividades para la difusión y fortalecimiento de la participación ciudadana y educación cívica.', '5');
