INSERT INTO `programasesp` VALUES ('58', '3', '4', 'E3 0304', 'Capacitación Permanente', 'Fomentar el desarrollo académico y de profesionalización del personal de Organismo Público Local Electoral del Estado de Veracruz.', '10');
